import styled from 'styled-components'
import { LinkError } from 'apollo-link/lib/linkUtils';

const PostStyles = styled.div`
  max-width: 100%;
  background-color: white;
  border: 1px solid ${({ theme }) => theme.darkGrey };
  margin-bottom: 6rem;
  display: flex;
  flex-direction: column;
  border-radius: 5px;
  div.post-top {
    display: flex; 
    justify-content: space-between; 
    padding: .5rem 1.5rem;
    p {
      font-size: 1.2rem;
      text-transform: lowercase;
    }
  }
  img {
    max-width:100%;
    height:auto;
  }
  div.post-bottom {
    display: flex;
    flex-direction: column;
    padding: .5rem 1.5rem;
    div.bottom-icons {
      display: flex;
      div:not(:first-child) {
        margin-left: 1rem;
        font-size: 2rem;
      }
    }
  }
`

const Post = props => {
  console.log(props)
  return (
    <PostStyles>
      <div className="post-top">
        <div>
          <p>👱 {props.owner.username}</p>
        </div>
        <div>
          <p>▪▪▪</p>
        </div>
      </div>
      <div>
        <img src={props.image} alt={props.image} />
      </div>
      <div className="post-bottom">
        <div className="bottom-icons">
          <div>❤</div>
          <div>💬</div>  
        </div>
        <p>{props.likes.length} likes</p>
      </div>
    </PostStyles>
  )
}

export default Post
